import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import axios from 'axios';
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Container = styled.div`
  width: 100%;
  max-width: 800px;
  margin-top: 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 10px;
`;

const Title = styled.h2``;

function NoticeBoard() {
  const [notices, setNotices] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchNotices();
  }, []);

  const fetchNotices = async () => {
    setLoading(true);
    try {
      const response = await axios.get('https://node-postgres-reactnative.onrender.com/notice');
      setNotices(response.data);
      setLoading(false);
    } catch (error) {
      toast.error('Error fetching notices: ' + error.message);
      setLoading(false);
    }
  };

  return (
    <>
      <Container>
        <Title>Mural de Avisos</Title>
        {loading ? <p>Loading...</p> : (
          notices.map((notice) => (
            <div key={notice.id} style={{ background: '#fff', padding: '20px', borderRadius: '5px', margin: '10px', boxShadow: '0px 0px 5px #ccc' }}>
              <h3>{notice.title}</h3>
              <p>{notice.description}</p>
            </div>
          ))
        )}
      </Container>
      <ToastContainer autoClose={3000} position={toast.POSITION.BOTTOM_LEFT} />
    </>
  );
}

export default NoticeBoard;
